import schedule from '../../../data/Schedule.json';
import film from '../../../data/Film.json';

// let userDayAndPlanKeys;
let filteredFilms;

function filterScheduleAndExtractKeys(scheduleData) {
    const filteredUsers = scheduleData.filter(user =>
      Object.keys(user.plan).some(time => user.plan[time] === false ) //знаходимо дні, де є вільна година у користувача
    );


    const userDayAndPlanKeys = filteredUsers.map(user => ({
      day: user.day,
      planKeys: Object.keys(user.plan)
    }));

    return userDayAndPlanKeys;
  }
  console.log(userDayAndPlanKeys);
  filterScheduleAndExtractKeys(schedule)

  function filterFilmsByDayAndTime(filmsData, userDayAndPlanKeys) {
    filteredFilms = filmsData.filter(movie =>
      userDayAndPlanKeys.some(user =>
        movie.Date.day === user.day && user.planKeys.includes(movie.Date.time)
      )
    );

    return filteredFilms;
  }


